import Button from 'react-bootstrap/Button';
import { BsPlusLg, BsAppIndicator, BsSearch } from "react-icons/bs";
import axios from 'axios';
import { Link } from "react-router-dom";
import Cards from './Cards';
import React, { useState, useEffect } from "react";

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// import Dropdown from 'react-bootstrap/Dropdown';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';


function Header() {
    const [serchValue, setSearchValue] = useState('');
    const [filterValue, setFilterValue] = useState('');
    const [isChecked, setChecked] = useState(false);

    const options = [
        'Profit', 'Loss'
    ]

    function serchProducts(data) {
        setSearchValue(data.target.value)
    }

    const defaultOption = options[0];
    function getProductByFilter(element) {
        console.log("Form.Control", element);
        // setFilterValue(value);
    }

    function handleChange (e){
        console.log("Form.Control", element);

    }
    return (
        <>
            <Navbar bg="light" expand="lg">
                <Container fluid>
                    <Row className='w-100'>
                        <Col md={4}>
                            <h2> Web Sultanate </h2>
                        </Col>
                        <Col md={2}>
                            <Link to="/add-product" style={{ 'color': 'white', 'textDecoration': 'none' }}><Button variant="success"><BsPlusLg /> Add Products</Button></Link>
                        </Col>
                        <Col md={1} style={{'fontSize': 'larger', 'textAlign': 'end', 'lineHeight': '35px'}}>
                            <Dropdown options={options} value={defaultOption} onChange={e => handleChange(e)} />
                        </Col>
                        <Col md={2}>
                            <Form.Control onChange={(e) => getProductByFilter(e.value)} placeholder="Filter By %" />
                        </Col>
                        <Col md={3}>
                            <Form className="d-flex nav navbar-nav navbar-right">
                                <Form.Control
                                    type="search"
                                    placeholder="Search"
                                    aria-label="Search" style={{ width: '80%' }}
                                    onKeyUp={(e) => serchProducts(e)}
                                />
                                <div style={{ fontSize: 'larger', 'marginLeft': '3%' }}>
                                    <BsSearch />
                                </div>
                            </Form>
                        </Col>
                    </Row>
                </Container>
            </Navbar>
            < Cards serchValue={serchValue} filterValue={filterValue} isChecked={isChecked}/>
        </>
    );
}

export default Header;
