import Button from 'react-bootstrap/Button';
import { BsPlusLg, BsAppIndicator, BsSearch } from "react-icons/bs";
import axios from 'axios';

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// import Dropdown from 'react-bootstrap/Dropdown';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';


    // position: sticky;top: 0
    // <BsPlusLg /> Add Products 
    // AiOutlineSearch
function Header() {

    const options = [
        'Profit 40% to 35%', 'Profit 35% to 30%', 'Profit 30% to 25%', 'Profit 25% to 20%',
        'Profit 20% to 15%', 'Profit 15% to 10%',
        'Loss 30% to 25%', 'Loss 25% to 20%', 'Loss 20% to 15%', 'Loss 15% to 10%',
        'Loss 10% to 5%'  
        ]
    // const defaultOption = options[0];
    function getProductByFilter(element){
        console.log("gggggggggggggggg", element);
        let firstValue;
        let ifProfit = false;
        if(element.indexOf('Profit') !== -1){
            firstValue = element.slice(7, 9);
            ifProfit = true;
            console.log("firstValuePRo", firstValue);
        } else if(element.indexOf('Loss') !== -1){
            firstValue = element.slice(5, 7);
            console.log("firstValueLoss", firstValue);
        }
        if(firstValue){
            let data = {
                firstValue: firstValue,
                ifProfit: ifProfit
            }
            axios({
            url: "http://localhost:3600/common/getFilteredProducts",
            method: "GET",
            data: firstValue,
          }).then((res) => {
            console.log("getFilteredProducts", res);
          }).catch((err) => { console.log("err", err); });
        }
    }

    return (
        <>     
            <Navbar bg="light" expand="lg">
                <Container fluid>
                    <Row className='w-100'>
                    <Col xs={7}>
                        <h2> Web Sultanate </h2>
                    </Col>
                    <Col xs={2}>
                        <Dropdown options={options} onChange={(e) => getProductByFilter(e.value)} placeholder="Select an option" />
                    </Col>
                    <Col className="offset-xs-3">
                        <Navbar.Collapse id="navbarScroll">
                            <Navbar.Brand href="#">hg</Navbar.Brand>
                            <Form className="d-flex nav navbar-nav navbar-right">
                                <Form.Control
                                    type="search"
                                    placeholder= "search"
                                    className="me-2"
                                    aria-label="Search"
                                />
                            </Form>
                            <div style = {{ fontSize: 'larger' }}>
                                <BsSearch />
                            </div>
                        </Navbar.Collapse>
                    </Col>
                    </Row>
                </Container>
            </Navbar>
        </>
    );
}

export default Header;
