import Button from 'react-bootstrap/Button';
import { BsPlusLg, BsAppIndicator, BsSearch } from "react-icons/bs";
import axios from 'axios';
import { Link } from "react-router-dom";
import Cards  from './Cards';

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// import Dropdown from 'react-bootstrap/Dropdown';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';


// position: sticky;top: 0
// <BsPlusLg /> Add Products 
// AiOutlineSearch
function Header() {

    const options = [
        'Profit 40% to 35%', 'Profit 35% to 30%', 'Profit 30% to 25%', 'Profit 25% to 20%',
        'Profit 20% to 15%', 'Profit 15% to 10%',
        'Loss 30% to 25%', 'Loss 25% to 20%', 'Loss 20% to 15%', 'Loss 15% to 10%',
    ]

    function serchProducts (data){
        console.log("gggggggggggggggg", data.target.value);
    }

    // const defaultOption = options[0];
    function getProductByFilter(element) {
        console.log("gggggggggggggggg", element);
        let firstValue;
        axios({
            url: "$.SERVER_BASE_URL/common/getAllProducts",
            method: "GET",
            // data: data,
        }).then((res) => {
            console.log("getFilteredProducts", res);
            let allProductsArray = res.data ? res.data.allProducts ? res.data.allProducts : [] : [];
            if (element.indexOf('Profit') !== -1) {
                firstValue = element.slice(7, 9);
                allProductsArray = allProductsArray.filter(e => {
                    let maxProfitAmount = (e.buyPrice * firstValue) / 100
                    let minProfitAmount = (e.buyPrice * (firstValue - 5)) / 100

                    console.log("sellPrice", e.sellPrice, ">>>>>buyPrice", e.buyPrice +  minProfitAmount,"&&&&&&&&&&77sellPic", e.sellPrice,"<<<<<<max", maxProfitAmount + e.buyPrice);
                    if(e.sellPrice > (e.buyPrice + minProfitAmount) && e.sellPrice < (maxProfitAmount + e.buyPrice)){
                        console.log("true");
                        return e
                    }
                })
                console.log("firstValuePRo", allProductsArray);
            } else if (element.indexOf('Loss') !== -1) {
                firstValue = element.slice(5, 7);
                console.log("firstValueLoss", firstValue);
            }

        }).catch((err) => { console.log("err", err); });
    }

    return (
        <>
            <Navbar bg="light" expand="lg">  {/* style={{position: 'sticky', top: 0}} */}
                <Container fluid>
                    <Row className='w-100'>
                        <Col md={5}>
                            <h2> Web Sultanate </h2>
                        </Col>
                        <Col md={2}>
                        <Link to="/add-product" style={{'color': 'white', 'textDecoration': 'none'}}><Button variant="success"><BsPlusLg /> Add Products</Button></Link>
                        </Col>
                        <Col md={2}>
                            {/* <Navbar.Brand href="#" onClick={(e) => addProducts}>Add</Navbar.Brand> */}
                            <Dropdown options={options} onChange={(e) => getProductByFilter(e.value)} placeholder="Select " style={{'cursor': 'pointer'}} />
                        </Col>
                        <Col md={3}>
                            <Form className="d-flex nav navbar-nav navbar-right">
                                <Form.Control
                                    type="search"
                                    placeholder="Search"
                                    aria-label="Search" style={{width: '80%'}}
                                    onKeyUp={(e) => serchProducts(e)}
                                />
                                <div style={{ fontSize: 'larger', 'marginLeft': '3%' }}>
                                    <BsSearch />
                                </div>
                            </Form>
                        </Col>
                    </Row>
                </Container>
            </Navbar>
            < Cards />
        </>
    );
}

export default Header;
