import axios from 'axios';
import React, { useState, useEffect } from "react";

import {Button, Card, Row, Container} from 'react-bootstrap/';

function Cards() {
  const [products, setAllProducts] = useState([]);
  const [count, setCount] = useState(0);
  let allProductsArray

  function saveProduct(e) {
    console.log("Ddddddddddd", e);
    let data = {
      Name: 'shirt e4',
      Category: 'cloths',
      buyPrice: 650,
      sellPrice: 899,
    }
    axios({
      url: "http://localhost:3600/common/saveProduct",
      method: "POST",
      data: data,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
    }).catch((err) => { console.log("ee", err); });
  }
  useEffect(() => {
    axios({
      url: "http://localhost:3600/common/getAllProducts",
      method: "GET",
      async: false,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
      allProductsArray = res.data ? res.data.allProducts ? res.data.allProducts : [] : [];
      setAllProducts(allProductsArray);
      setCount(count + 2);
    }).catch((err) => { console.log("ee", err); });
  }, [])
  console.log("d===========", count, products);
  return (
    <>
    <Container>
      <Row>
        {products.length && products.map((item) => {
          return (
            <Card className='mr-4' style={{ width: '26rem' }} key={item.id}>
              <Card.Img variant="top" src="holder.js/100px180" />
              <Card.Body>
                <Card.Title>{item.Name}</Card.Title>
                <Card.Text>
                  Some quick example text to build on the card title and make up the
                  bulk of the card's content.
                  Price - {item.sellPrice}
                </Card.Text>
                <Button variant="primary" onClick={(e) => saveProduct(e)} >Go somewhere</Button>
              </Card.Body>
            </Card>
          )
        })}
      </Row>
    </Container>
    </>
  );
}

export default Cards;
