import Button from 'react-bootstrap/Button';
import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import axios from 'axios';
import { BsArrowLeft } from "react-icons/bs";

function Forms() {
    const navigate = useNavigate();
    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
      const form = event.currentTarget;
      console.log("top", form.checkValidity());
      if (form.checkValidity() === false) {
        console.log("errro", form.checkValidity());
        event.preventDefault();
        event.stopPropagation();
        setValidated(true);
      }else{
        event.preventDefault();
        event.stopPropagation();

        // setValidated(true);
        // event.preventDefault();
        // const {target} = event;
        // let obj = Object.fromEntries(new FormData(target))
        // console.log('FormData', obj);
        // console.log('target.user.value', target.name.value);
        // axios({
        //     url: "http://localhost:3600/common/saveProduct",
        //     method: "POST",
        //     data: obj,
        //   }).then((res) => {
        //     console.log("Dddddddsssssssss", res);
        //     alert('Product Added SuccessFully!')
        //     document.getElementById("addProductForm").reset();
        //   }).catch((err) => { console.log("ee", err); });
        return
      }

    };
    return (
        <>
            <Button variant="info" onClick={() => navigate(-1)} type="button" className='my-3 mx-4'>
                <BsArrowLeft /> Go Back
            </Button>
            <div style={{ 'display': 'flex', 'justifyContent': 'center', 'alignItems': 'center', 'padding': '30px', 'border': '1px solid #e1e1e1', 'borderRadius': '10px', 'margin': '2px 25%' }}>
                <Form noValidate validated={validated} onSubmit={handleSubmit} id='addProductForm'>
                    <h4 className='m-4' style={{'textAlign': 'center'}}>Add Product</h4>

                    <Row className="mb-3">
                        <Form.Group as={Col} md="6" controlId="validationCustom01">
                            <Form.Label>Product Name</Form.Label>
                            <Form.Control
                                // required
                                type="text"
                                name='Name'
                                placeholder="Enter Name"
                            />
                            <Form.Control.Feedback type="invalid">
                                Please provide a Product Name.
                            </Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group as={Col} md="6" controlId="validationCustom02">
                            <Form.Label>Category</Form.Label>
                            <Form.Control
                                // required
                                type="text"
                                name='Category'
                                placeholder="Enter Category"
                            />
                            <Form.Control.Feedback type="invalid">
                                Please provide a Product Category.
                            </Form.Control.Feedback>
                        </Form.Group>
                    </Row>
                    <Row className="mb-3">
                        <Form.Group as={Col} md="4" controlId="validationCustom03">
                            <Form.Label>Buy Price</Form.Label>
                            <Form.Control type="text" name='buyPrice' pattern="[0-9]*" placeholder="Enter Buy Price" required />
                            <Form.Control.Feedback type="invalid">
                                Please Enter Valid Price 
                            </Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group as={Col} md="4" controlId="validationCustom04">
                            <Form.Label>Sell Price</Form.Label>
                            <Form.Control type="text" name='sellPrice' placeholder="Enter Sell Price" required />
                            <Form.Control.Feedback type="invalid">
                                Please provide a valid city.
                            </Form.Control.Feedback>
                        </Form.Group>
                    </Row>
                    <Button type="submit" className='my-4'>Add Product</Button>
                </Form>
            </div>
        </>
    );
}

export default Forms;
