import axios from 'axios';
import React, { useState, useEffect } from "react";

import { Button, Card, Row, Container, Col } from 'react-bootstrap/';

function Cards() {
  const [products, setAllProducts] = useState([]);
  const [count, setCount] = useState(0);
  let allProductsArray

  function saveProduct(e) {
    console.log("Ddddddddddd", e);
    let data = {
      Name: 'SWAROVSKI e5',
      Category: 'Eyewear',
      buyPrice: 3000,
      sellPrice: 4000,
    }
    axios({
      url: "http://localhost:3600/common/saveProduct",
      method: "POST",
      data: data,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
    }).catch((err) => { console.log("ee", err); });
  }
  useEffect(() => {
    axios({
      url: "http://localhost:3600/common/getAllProducts",
      method: "GET",
      async: false,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
      allProductsArray = res.data ? res.data.allProducts ? res.data.allProducts : [] : [];
      setAllProducts(allProductsArray);
      setCount(count + 2);
    }).catch((err) => { console.log("ee", err); });
  }, [])
  console.log("d===========", count, products);
  return (
    <>
      <Container style={{ 'positionY': 'hidden', 'height': 'calc(90vh - 30px)' }} fluid>
        <Row className='row px-5' style={{ 'overflowY': 'auto', 'height': 'calc(90vh - 30px)' }}>
          {products.length && products.map((item) => {
            return (
              <Col xs={3} key={item._id} className='mt-3 mb-1'>
                <Card style={{ 'position': 'initial' }}>
                  <Card.Body>
                    <Card.Title>{item.Name}</Card.Title>
                    <Card.Text>
                      {item.Category} <br></br>
                      Buy Price - {item.buyPrice} <br></br>
                      Sell Price - {item.sellPrice}
                    </Card.Text>
                    <Button variant="primary" onClick={(e) => saveProduct(e)} >Buy Now</Button>
                  </Card.Body>
                </Card>
              </Col>
            )
          })}
        </Row>
      </Container>
    </>
  );
}

export default Cards;
