import axios from 'axios';
import React, { useState, useEffect } from "react";
import InfiniteScroll from 'react-infinite-scroll-component';

import { Button, Card, Row, Container, Col } from 'react-bootstrap/';

function Cards() {
  const [products, setAllProducts] = useState([]);
  const [count, setCount] = useState(1);
  let allProductsArray
  // console.log("kkkkkkk=========", window.pageYOffset);
  // useEffect(() => {
  //   window.addEventListener('scroll', () => {
  //     console.log("fllllllllllllldddddddddd", window.scrollY);
  //     if (window.scrollY > 400) {
  //       // setShowTopBtn(true);
  //     } else {
  //       // setShowTopBtn(false);
  //     }
  //   });
  // }, []);
  function getProductsData(){
    axios({
      url: `${window.SERVER_BASE_URL}/common/getAllProducts`,
      method: "GET",
      async: false,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
      allProductsArray = res.data ? res.data.allProducts ? res.data.allProducts : [] : [];
      setAllProducts(allProductsArray);
      setCount(count + 12);
    }).catch((err) => { console.log("ee", err); });
  }
  function listenScrollEvent(e) {
    setCount(count + 12);

    console.log("fffffffffffffffffffffffffffffff");
    // const scrollY = window.scrollY 
    // axios({
    //   url: "$.SERVER_BASE_URL/common/saveProduct",
    //   method: "POST",
    //   data: data,
    // }).then((res) => {
    //   console.log("Dddddddsssssssss", res);
    // }).catch((err) => { console.log("ee", err); });
  }
  useEffect(() => {
    getProductsData()
  }, [])
  // console.log("d===========", window.SERVER_BASE_URL, count, products);
  return (
    <>
      <Container style={{ 'positionY': 'hidden', 'height': 'calc(90vh - 30px)' }} fluid>
        <div
          id="scrollableDiv"
          style={{
            height: 300,
            overflow: 'auto',
            display: 'flex',
            flexDirection: 'column-reverse',
          }}
        >
          <div style={{ 'overflowY': 'auto', 'height': 'calc(90vh - 30px)' }}>
          {/*Put the scroll bar always on the bottom*/}
          <InfiniteScroll
            dataLength={count}
            next={listenScrollEvent}
            // style={{ display: 'flex', flexDirection: 'column-reverse' }} //To put endMessage and loader to the top.
            // inverse={true} //
            hasMore={true}
            // loader={<h4>Loading...</h4>}
            scrollableTarget="sssssssss"
          >
            <Row className='row px-5' id='sssssssss'>
              {products.length && products.map((item) => {
                return (
                  <Col md={3} key={item._id} className='mt-4 mb-2'>
                    <Card style={{ 'position': 'initial', 'backgroundColor': '#f4f4ff' }}>
                      <Card.Body>
                        <Card.Title>{item.Name}</Card.Title>
                        <Card.Text>
                          {item.Category} <br></br>
                          Buy Price - {item.buyPrice} <br></br>
                          Sell Price - {item.sellPrice}
                        </Card.Text>
                        <Button variant="primary" >Buy Now</Button>
                      </Card.Body>
                    </Card>
                  </Col>
                )
              })}
            </Row>
          </InfiniteScroll>
          </div>
        </div>

      </Container>
    </>
  );
}

export default Cards;
