import Button from 'react-bootstrap/Button';
import { BsPlusLg, BsAppIndicator, BsSearch } from "react-icons/bs";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Dropdown from 'react-bootstrap/Dropdown';
import InputGroup from 'react-bootstrap/InputGroup';
import SplitButton from 'react-bootstrap/SplitButton';


    // position: sticky;top: 0
{/* <BsPlusLg /> Add Products */}
    // AiOutlineSearch
function Header() {
    return (
        <>     
            <Navbar bg="light" expand="lg">
                <Container fluid>
                    <Row className='w-100'>
                    <Col xs={3}>
                        <h2> Web Cunsultancy </h2>
                    </Col>
                    <Col >
                        <InputGroup>
                            <SplitButton
                            variant="outline-secondary"
                            title="Action"
                            id="segmented-button-dropdown-1"
                            >
                            <Dropdown.Item href="#">Action</Dropdown.Item>
                            <Dropdown.Item href="#">Another action</Dropdown.Item>
                            <Dropdown.Item href="#">Something else here</Dropdown.Item>
                            <Dropdown.Divider />
                            <Dropdown.Item href="#">Separated link</Dropdown.Item>
                            </SplitButton>
                            <Form.Control className="me-2" aria-label="Text input with dropdown button" />
                        </InputGroup>
                    </Col>
                    <Col xs={3}>
                        <Navbar.Collapse id="navbarScroll">
                            <Navbar.Brand href="#"></Navbar.Brand>
                            <Form className="d-flex nav navbar-nav navbar-right">
                                <Form.Control
                                    type="search"
                                    placeholder= "search"
                                    className="me-2"
                                    aria-label="Search"
                                />
                            </Form>
                            <div style = {{ fontSize: 'larger' }}>
                                <BsSearch />
                            </div>
                        </Navbar.Collapse>
                    </Col>
                    </Row>
                </Container>
            </Navbar>
        </>
    );
}

export default Header;
