import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import axios from 'axios';

function Cards() {
  let allProducts = [];
  fetch("http://localhost:3600/common/getAllProducts")
    .then(res => res.json())
    .then(
      (result) => {
        console.log("ffffffffffffgotitg", result);
        allProducts = result ? result.allProducts ? result.allProducts: []: [];
      },
      (error) => {
        console.error('ww')
      }
    )
  function saveProduct(e) {
    console.log("Ddddddddddd", e);
    let data = {
    Name: 'shirt e4',
    Category: 'cloths',
    buyPrice: 650,
    sellPrice: 899,
    }
    axios({
      url: "http://localhost:3600/common/saveProduct",
      method: "POST",
      data: data,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
    }).catch((err) => { console.log("ee", err); });
  }
  return (
    <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="holder.js/100px180" />
      <Card.Body>
        <Card.Title>Card Title</Card.Title>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
        <Button variant="primary" onClick={(e) => saveProduct(e)}>Go somewhere</Button>
      </Card.Body>
    </Card>
  );
}

export default Cards;
