import axios from 'axios';
import React, { useState, useEffect } from "react";

import { Button, Card, Row, Container, Col } from 'react-bootstrap/';

function Cards() {
  const [products, setAllProducts] = useState([]);
  const [count, setCount] = useState(0);
  let allProductsArray
  // console.log("kkkkkkk=========", window.pageYOffset);
  // useEffect(() => {
  //   window.addEventListener('scroll', () => {
  //     console.log("fllllllllllllldddddddddd", window.scrollY);
  //     if (window.scrollY > 400) {
  //       // setShowTopBtn(true);
  //     } else {
  //       // setShowTopBtn(false);
  //     }
  //   });
  // }, []);
  function getProductsData(){
    axios({
      url: `${window.SERVER_BASE_URL}/common/getAllProducts`,
      method: "GET",
      async: false,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
      allProductsArray = res.data ? res.data.allProducts ? res.data.allProducts : [] : [];
      setAllProducts(allProductsArray);
      setCount(count + 2);
    }).catch((err) => { console.log("ee", err); });
  }
  function listenScrollEvent(e) {
  console.log("d===========", document.documentElement.scrollHeight, document.body.scrollTop);

    // const scrollY = window.scrollY 
    // axios({
    //   url: "$.SERVER_BASE_URL/common/saveProduct",
    //   method: "POST",
    //   data: data,
    // }).then((res) => {
    //   console.log("Dddddddsssssssss", res);
    // }).catch((err) => { console.log("ee", err); });
  }
  useEffect(() => {
    getProductsData()
  }, [])
  return (
    <>
      <Container style={{ 'positionY': 'hidden', 'height': 'calc(90vh - 30px)' }} fluid>
            <Row className='row px-5' onScroll={listenScrollEvent} style={{ 'overflowY': 'auto', 'height': 'calc(90vh - 30px)' }}>
              {products.length && products.map((item) => {
                return (
                  <Col md={3} key={item._id} className='mt-4 mb-2'>
                    <Card style={{ 'position': 'initial', 'backgroundColor': '#f4f4ff' }}>
                      <Card.Body>
                        <Card.Title>{item.Name}</Card.Title>
                        <Card.Text>
                          {item.Category} <br></br>
                          Buy Price - {item.buyPrice} <br></br>
                          Sell Price - {item.sellPrice}
                        </Card.Text>
                        <Button variant="primary" >Buy Now</Button>
                      </Card.Body>
                    </Card>
                  </Col>
                )
              })}
            </Row>
      </Container>
    </>
  );
}

export default Cards;
