import axios from 'axios';
import React, { useState, useEffect } from "react";

import {Button, Card, Row, Container} from 'react-bootstrap/';

function Cards() {
  const [products, setAllProducts] = useState([]);
  const [count, setCount] = useState(0);
  let allProductsArray

  function saveProduct(e) {
    console.log("Ddddddddddd", e);
    let data = {
      Name: 'SWAROVSKI e5',
      Category: 'Eyewear',
      buyPrice: 3000,
      sellPrice: 4000,
    }
    axios({
      url: "http://localhost:3600/common/saveProduct",
      method: "POST",
      data: data,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
    }).catch((err) => { console.log("ee", err); });
  }
  useEffect(() => {
    axios({
      url: "http://localhost:3600/common/getAllProducts",
      method: "GET",
      async: false,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
      allProductsArray = res.data ? res.data.allProducts ? res.data.allProducts : [] : [];
      setAllProducts(allProductsArray);
      setCount(count + 2);
    }).catch((err) => { console.log("ee", err); });
  }, [])
  console.log("d===========", count, products);
  return (
    <>
    <Container style={{'position-y': 'hidden', 'height': 'calc(90vh - 50px)' }}>
      <Row className='' style={{'overflow-y': 'auto', 'height': 'calc(90vh - 50px)'}}>
        {products.length && products.map((item) => {
          return (
            <Card className='mr-5 mt-5' style={{ width: '23rem', 'position': 'initial' }} key={item.id}>
              <Card.Body>
                <Card.Title>{item.Name}</Card.Title>
                <Card.Text>
                  {item.Category} <br></br>
                  Buy Price - {item.buyPrice} <br></br>
                  Sell Price - {item.sellPrice}
                </Card.Text>
                <Button variant="primary" onClick={(e) => saveProduct(e)} >Buy Now</Button>
              </Card.Body>
            </Card>
          )
        })}
      </Row>
    </Container>
    </>
  );
}

export default Cards;
