import Button from 'react-bootstrap/Button';
import { BsPlusLg, BsAppIndicator, BsSearch } from "react-icons/bs";
import axios from 'axios';
import { Link } from "react-router-dom";
import Cards from './Cards';
import React, { useState, useEffect } from "react";

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// import Dropdown from 'react-bootstrap/Dropdown';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';


function Header() {
    const [serchValue, setSearchValue] = useState('');
    const [filterValue, setFilterValue] = useState('');
    const [isChecked, setChecked] = useState(false);

    const options = [
        '45% to 40%', '40% to 35%', '35% to 30%', '30% to 25%', '25% to 20%',
        '20% to 15%', '15% to 10%', '10% to 5%',
    ]

    function serchProducts(data) {
        setSearchValue(data.target.value)
    }

    // const defaultOption = options[0];
    function getProductByFilter(element) {
        let value = element.slice(0, 2)
        setFilterValue(value);
    }

    function handleChange (e){
        setChecked(e.target.checked)
    }
    return (
        <>
            <Navbar bg="light" expand="lg">  {/* style={{position: 'sticky', top: 0}} */}
                <Container fluid>
                    <Row className='w-100'>
                        <Col md={4}>
                            <h2> Web Sultanate </h2>
                        </Col>
                        <Col md={2}>
                            <Link to="/add-product" style={{ 'color': 'white', 'textDecoration': 'none' }}><Button variant="success"><BsPlusLg /> Add Products</Button></Link>
                        </Col>
                        <Col md={1} style={{'fontSize': 'larger', 'textAlign': 'end', 'lineHeight': '35px'}}>
                            <input type="checkbox" onChange={e => handleChange(e)} style={{'width': '15px', 'height': '15px', 'cursor': 'pointer'}} /> <label> Loss </label>
                        </Col>
                        <Col md={2}>
                            {/* <Navbar.Brand href="#" onClick={(e) => addProducts}>Add</Navbar.Brand> */}
                            <Dropdown options={options} onChange={(e) => getProductByFilter(e.value)} placeholder="Filter" style={{ 'cursor': 'pointer' }} />
                        </Col>
                        <Col md={3}>
                            <Form className="d-flex nav navbar-nav navbar-right">
                                <Form.Control
                                    type="search"
                                    placeholder="Search"
                                    aria-label="Search" style={{ width: '80%' }}
                                    onKeyUp={(e) => serchProducts(e)}
                                />
                                <div style={{ fontSize: 'larger', 'marginLeft': '3%' }}>
                                    <BsSearch />
                                </div>
                            </Form>
                        </Col>
                    </Row>
                </Container>
            </Navbar>
            < Cards serchValue={serchValue} filterValue={filterValue} isChecked={isChecked}/>
        </>
    );
}

export default Header;
