import axios from 'axios';
import React, { useState, useEffect } from "react";

import { Button, Card, Row, Container, Col } from 'react-bootstrap/';

function Cards() {
  const [products, setAllProducts] = useState([]);
  const [count, setCount] = useState(0);
  let offSet = 0
  let pageScrolledTill = 200;
  function getProductsData(offSet){
    axios({
      url: `${window.SERVER_BASE_URL}/common/getAllProducts?offSet=${offSet}`,
      method: "GET",
      async: false,
    }).then((res) => {
      console.log("Dddddddsssssssss", res);
      let allProductsArray = res.data ? res.data.allProducts ? res.data.allProducts : [] : [];
      setAllProducts(allProductsArray);
      setCount(count + 2);
    }).catch((err) => { console.log("ee", err); });
  }
  function listenScrollEvent(e) {
  const scroller = document.querySelector("#scroller");

  console.log("d===========", pageScrolledTill, scroller.scrollTop);
  if(scroller.scrollTop > pageScrolledTill){
    pageScrolledTill = pageScrolledTill + 150;
    console.log("gggggggggg", pageScrolledTill, products.length);
    getProductsData(products.length);
  }

  }
  useEffect(() => {
    getProductsData()
  }, [])
  return (
    <>
      <Container style={{ 'positionY': 'hidden', 'height': 'calc(90vh - 30px)' }} fluid>
            <Row className='row px-5' onScroll={listenScrollEvent} id="scroller" style={{ 'overflowY': 'auto', 'height': 'calc(90vh - 30px)' }}>
              {products.length && products.map((item) => {
                return (
                  <Col md={3} key={item._id} className='mt-4 mb-2'>
                    <Card style={{ 'position': 'initial', 'backgroundColor': '#f4f4ff' }}>
                      <Card.Body>
                        <Card.Title>{item.Name}</Card.Title>
                        <Card.Text>
                          {item.Category} <br></br>
                          Buy Price - {item.buyPrice} <br></br>
                          Sell Price - {item.sellPrice}
                        </Card.Text>
                        <Button variant="primary" >Buy Now</Button>
                      </Card.Body>
                    </Card>
                  </Col>
                )
              })}
            </Row>
      </Container>
    </>
  );
}

export default Cards;
